import React from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Container from '@material-ui/core/Container';
import Typography from '@material-ui/core/Typography';
import Alert from '@material-ui/lab/Alert';

import { loginUser } from './usersService';
import { connect } from 'react-redux';
import { app_login } from '../redux/actions/userActions';
import {getPolicyByPatient} from '../physician/physicianService';
import insurance from '../serviceClient/apiInsurance';

class Login extends React.Component {
    state = {
        username: "",
        password: "",
        error: "",
    }

    test = async(e) => {
        e.preventDefault();
        let payload = { 
            visitation_id: 20,
            physician_id: 8,
            patient_id: 11,
            visitation_date: '2021-04-18T22: 30: 37.000Z',
            status: 2,
            patient_first: 'Jimmy',
            patient_last: 'Jeremy',
            physician_first: 'Meg',
            physician_last: 'White',
            address: '123 Street',
            date_of_birth: '1980-01-01',
            physician_state: 'MI',
            license_number: '152352',
            procedures: [
                  { visitation_id: 30,
                 procedure_id: '90785-90899',
                 insurance_pays: null,
                 name: 'Psychiatry Services and Procedures',
                 price: 350
                  },
                  { visitation_id: 30,
                 procedure_id: '92920-93799',
                 insurance_pays: null,
                 name: 'Cardiovascular Procedures',
                 price: 450
                  }
              ]
          };
          insurance.post('/policy/getPolicyByPatient', payload).then(res => {
            console.log(res);
        });
    }

    login = async (e) => {
        e.preventDefault();
        // log the user in and go home
        if (!this.state.username || this.state.username === "") {
            this.setState({ error: "Username is empty" });
            return;
        }
        if (!this.state.password || this.state.password === "") {
            this.setState({ error: "Password is empty" });
            return;
        }
        let res = await loginUser(this.state.username, this.state.password);
        if (res.msg) {
            this.setState({ error: res.msg });
        }
        else {
            this.props.app_login(res.user);
            this.props.history.push('/security-question');
        }
    }

    changeForm = (e) => {
        let propName = e.target.name;
        let propValue = e.target.value;
        let state = { ...this.state };
        state[propName] = propValue;
        this.setState(state);
    }

    render() {
        const classes = {
            paper: {
                marginTop: "10px",
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
            },
            form: {
                width: '100%',
                marginTop: "10px",
            },
            submit: {
                margin: "10px",
            },
        };
        let alert = this.state.error ? <Alert severity="error">{this.state.error}</Alert> : "";

        return (
            <Container component="main" maxWidth="xs">
                <div style={classes.paper}>
                    <Avatar />
                    <Typography component="h1" variant="h5">Login</Typography>
                    <form style={classes.form} noValidate>
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            id="username"
                            label="Username"
                            name="username"
                            autoComplete="username"
                            autoFocus
                            onChange={this.changeForm}
                            value={this.state.username}
                        />
                        <TextField
                            variant="outlined"
                            margin="normal"
                            required
                            fullWidth
                            name="password"
                            label="Password"
                            type="password"
                            id="password"
                            autoComplete="current-password"
                            onChange={this.changeForm}
                            value={this.state.password}
                        />

                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            color="primary"
                            style={classes.submit}
                            onClick={this.login}>
                            Log In
                        </Button>
                    </form>
                    {alert}
                </div>
                <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            color="primary"
                            style={classes.submit}
                            onClick={this.test}>
                            Test
                        </Button>
            </Container>
        );
    }
}

const mapState = (state) => { return { user: state.user } };
const mapDispatch = { app_login };

export default connect(mapState, mapDispatch)(Login);
