import * as React from 'react';
import MaterialTable from 'material-table';
import { Select, MenuItem } from '@material-ui/core'
import { getUsers, patchUser } from '../auth/usersService';
import { User_Type_Admin, User_Type_Physician, User_Type_Patient, User_Status_Active, User_Status_Inactive, User_Status_Disabled } from '../models/user';

export default class UserManager extends React.Component {
    state = {
        users: []
    }

    componentDidMount() {
        this.getData();
    }

    getData = () => {
        let url = 'http://localhost:3030/users'
        return getUsers()
            .then(users => {
                this.setState({ users: users })
            })
    }

    render() {
        const tableRef = React.createRef();
        return (
            <MaterialTable
                tableRef={tableRef}
                options={{
                    sorting: true,
                    search: true,
                    paging: false,
                }}
                title="Users"
                columns={[
                    // { title: 'Id', field: '_id' },
                    { title: 'Username', field: 'username', editable: false },
                    { title: 'First Name', field: 'first_name' },
                    { title: 'Last Name', field: 'last_name' },
                    {
                        title: 'User Role',
                        field: 'userRole',
                        editComponent: props => (
                            <Select
                                id="userrole"
                                value={props.value || false}
                                onChange={e => props.onChange(e.target.value)}
                            >
                                <MenuItem value={User_Status_Active}>active</MenuItem>
                                <MenuItem value={User_Status_Inactive}>inactive</MenuItem>
                                <MenuItem value={User_Status_Disabled}>disabled</MenuItem>
                            </Select>),
                    }
                ]}
                data={this.state.users}
                editable={{
                    onRowUpdate: (newData, oldData) => {
                        return patchUser(newData).then(_ => this.getData())
                    },
                }}
            />
        )
    }

}
