import React from 'react';
import Typography from '@material-ui/core/Typography';
import { connect } from 'react-redux';
import { app_login } from '../redux/actions/userActions';
import { getPrescription } from './prescriptionsService';
import Icon from '@material-ui/core/Icon';
import { Button, Container, Col, Row } from 'react-bootstrap';
import Alert from '@material-ui/lab/Alert';
import MaterialTable from 'material-table';
import TextField from '@material-ui/core/TextField';
import { createPatient, getPatients, patchPatient, deletePatient } from './patientsService';
import US_States from '../models/states';
import { Select, MenuItem } from '@material-ui/core'

const initState = {
    prescription: ""
}

export class VerifyPatient extends React.Component {

    state = {
        ...initState,
        prescription: "",
        patients: []
    }

    changeForm = (e) => {
        let propName = e.target.name;
        let propValue = e.target.value;
        let state = { ...this.state };
        state[propName] = propValue;
        this.setState(state);
    }

    async componentDidMount() {
        const prescription_id = this.props.match.params.prescription;
        const prescription = await getPrescription(prescription_id);
        if (prescription.error) {
            this.setState({ error: prescription.error });
            return;
        }
        let state = { ...this.state };
        state['prescription'] = prescription;
        this.setState(state);
    }
    render() {
        const tableRef = React.createRef();
        const prescription = this.state.prescription;
        return (
            <Container className="card card-body bg-light">
                <Row>
                    <Col>
                        <MaterialTable
                            tableRef={tableRef}
                            options={{
                                sorting: true,
                                search: true,
                                paging: false,
                                editable: true,
                            }}
                            title="Patients"
                            columns={[
                                { title: 'First name', field: 'first_name' },
                                { title: 'Last name', field: 'last_name' },
                                { title: 'Date of birth', type: 'date', field: 'birth_date' },
                                { title: 'Address', field: 'address' },
                                { title: 'City', field: 'city' },
                                { title: 'State', field: 'state' },
                                { title: 'Zipcode', field: 'zipcode' },
                            ]}
                            data={this.state.patients}
                        />
                    </Col>
                </Row>
                <Row>
                    <Col><b>Patient Name</b></Col>
                    <Col>{prescription.patient_first_name} {prescription.patient_last_name}</Col>
                    <Col>{prescription.patient_first_name} {prescription.patient_last_name}</Col>
                </Row>
                <Row>
                    <Col><b>Date of Birth</b></Col>
                    <Col>{prescription.patient_date_of_birth}</Col>
                    <Col>{prescription.patient_date_of_birth}</Col>
                </Row>
                <Row>
                    <Col><b>Address</b></Col>
                    <Col>{prescription.patient_address} {prescription.patient_address}</Col>
                    <Col>{prescription.patient_address} {prescription.patient_address}</Col>
                </Row>
                <Row>
                    <Col>
                        <Button>Verify</Button>
                    </Col>
                </Row>
            </Container>
        );
    }


}

const mapState = (state) => { return { user: state.user } };
const mapDispatch = { app_login };

export default connect(mapState, mapDispatch)(VerifyPatient);
