import * as React from 'react';
import MaterialTable from 'material-table';
import { getPatients, patchPatient } from './patientsService';

export default class PatientManager extends React.Component {
    state = {
        patients: []
    }

    componentDidMount() {
        this.getData();
    }

    getData = () => {
        return getPatients()
            .then(patients => {
                this.setState({ patients: patients })
            })
    }

    render() {
        const tableRef = React.createRef();
        return (
            <div>
                <MaterialTable
                    tableRef={tableRef}
                    options={{
                        sorting: true,
                        search: true,
                        paging: false,
                    }}
                    title="Patients"
                    columns={[
                        // { title: 'Id', field: '_id' },
                        { title: 'First Name', field: 'first_name', validate: u => u.first_name == "" ? { isValid: false, helperText: "required" } : { isValid: true } },
                        { title: 'Last Name', field: 'last_name', validate: u => u.last_name == "" ? { isValid: false, helperText: "required" } : { isValid: true } },
                        { title: 'Date of Birth', field: 'birth_date', validate: u => u.birth_date == "" ? { isValid: false, helperText: "required" } : { isValid: true } },
                        { title: 'Address', field: 'address', validate: u => u.healthcare_institution == "" ? { isValid: false, helperText: "required" } : { isValid: true } },
                        { title: 'City', field: 'city', validate: u => u.city == "" ? { isValid: false, helperText: "required" } : { isValid: true } },
                        { title: 'Zipcode', field: 'zipcode', validate: u => u.zipcode == "" ? { isValid: false, helperText: "required" } : { isValid: true } },
                        { title: 'License Number', field: 'licence_number', validate: u => u.licence_number == "" ? { isValid: false, helperText: "required" } : { isValid: true } },
                    ]}
                    data={this.state.patients}
                    editable={{
                        onRowUpdate: (newData, oldData) => {
                            return patchPatient(newData.user_id, newData).then(_ => this.getData())
                        },
                    }}
                />
            </div>

        )
    }

}
