import * as React from 'react';
import MaterialTable from 'material-table';
import { Select, MenuItem } from '@material-ui/core'
import { getPhysicians, patchPhysician } from '../auth/usersService';
import { PhysicianType, PhysicianStatus } from '../models/user';
import { Navbar, Nav } from 'react-bootstrap';
import { Route, Link } from 'react-router-dom';

export default class PhysicianManager extends React.Component {
    state = {
        users: []
    }

    componentDidMount() {
        this.getData();
    }

    getData = () => {
        return getPhysicians()
            .then(users => {
                this.setState({ users: users })
            })
    }

    render() {
        const tableRef = React.createRef();
        return (
            <div>
                <Nav.Link as={Link} to="/register"><i className="fas fa-plus"></i> Register Physician</Nav.Link>
                <MaterialTable
                    tableRef={tableRef}
                    options={{
                        sorting: true,
                        search: true,
                        paging: false,
                    }}
                    title="Physicians"
                    columns={[
                        // { title: 'Id', field: '_id' },
                        { title: 'Physicianname', field: 'username', editable: false },
                        { title: 'First Name', field: 'first_name', validate: u => u.first_name == "" ? { isValid: false, helperText: "required" } : { isValid: true } },
                        { title: 'Last Name', field: 'last_name', validate: u => u.last_name == "" ? { isValid: false, helperText: "required" } : { isValid: true } },
                        {
                            title: 'Physician Type',
                            field: 'user_type',
                            editable: false,
                            render: u => <span>{PhysicianType.GetTranslation(u.user_type)}</span>,

                        },
                        {
                            title: 'Physician Status',
                            field: 'user_status',
                            render: u => <span>{PhysicianStatus.GetTranslation(u.user_status)}</span>,
                            editComponent: props => (
                                <Select
                                    id="user-status"
                                    value={props.value}
                                    onChange={e => props.onChange(e.target.value)}
                                >
                                    <MenuItem value={PhysicianStatus.Active}>{PhysicianStatus.GetTranslation(PhysicianStatus.Active)}</MenuItem>
                                    <MenuItem value={PhysicianStatus.Inactive}>{PhysicianStatus.GetTranslation(PhysicianStatus.Inactive)}</MenuItem>
                                    <MenuItem value={PhysicianStatus.Disabled}>{PhysicianStatus.GetTranslation(PhysicianStatus.Disabled)}</MenuItem>
                                </Select>),
                        }
                    ]}
                    data={this.state.users}
                    editable={{
                        onRowUpdate: (newData, oldData) => {
                            return patchPhysician(newData.user_id, newData).then(_ => this.getData())
                        },
                    }}
                />
            </div>

        )
    }

}
